import datetimeimport mathdef parent():    def child():        global age        age = 10        print(f"My age: {age * 3}")    child()    def currentDate():        my_date = datetime.datetime.now()        _year = my_date.year        _month = my_date.month        _minute = my_date.minute        _date = my_date.day        _show_all = f"Year: {_year}, Month: {_month}, minute: {_minute}, Day:{_date}"        print(_show_all)    currentDate()    print(f"Hello 'm parent method...\nMy child age: {age}")parent()print('\n')def calculation():    x = math.sqrt(64)    print(x)    y = math.floor(1.9)    print(y)    z = math.ceil(1.4)    print(z)calculation()