import googletransfrom googletrans import Translatortranslator = Translator()# print(googletrans.LANGUAGES)message = 'I love python programming...'message2 = 'আমি তোমাকে ভালোবাসি!'message3 = 'मैं तुमसे बहुत प्यार करता हु'print(translator.detect(message))print(translator.detect(message2))print(translator.detect(message3))print("English to hindi: ", translator.translate(message, src='en', dest='hi'))print("English to bangla: ", translator.translate(message, src='en', dest='bn'))