import requestsclass RequestAPI(object):    api_url = 'http://127.0.0.1:8001/api/disease/'    # Here the method using for fetch data from server    def fetch_data(self):        data = requests.get(self.api_url)        if data.status_code == 200:            return data.content        elif data.status_code == 404:            print(f"Not ok {data.status_code}")        else:            print('An error has occurred.')    """        Here, `send_data` method using for send data into server/database        :param args:        :param _data: except values dict format         :return: success and fail(True/False)    """    def send_data(self, _data):        data = requests.post(self.api_url, _data)        if data.status_code == 201:            return data        else:            return f"Sorry! please try again later,\n{data.status_code}"    # Update data    def update_data(self, new_data):        data = requests.put(self.api_url2, new_data)        if data.status_code == 202:            return data        else:            return f"Sorry! please try again later,\n{data.status_code}"    # Delete data    def delete_data(self, id):        data = requests.delete(self.api_url + id)        if data:            return data        else:            return data.status_codeif __name__ == '__main__':    request_instance = RequestAPI()    # Add new data    # user_input_data = input("Enter disease name: ")    # save = request_instance.send_data({'disease_name': user_input_data})    # print(save)    # Update data    # pk = input("Your post Id: ")    # new_user_data = input("Enter new data: ")    # put = request_instance.update_data({'disease_name': new_user_data})    # print(put)    post_id = input("Enter post id:")    _delete = request_instance.delete_data(post_id)    print(_delete)    print(request_instance.fetch_data())