class Person(object):    def __init__(self, name, age):        self.name = name        self.age = age    def about(self):        return f"My Name is: {self.name}\nMy age is: {self.age}"class Teacher(Person):    def __init__(self, name, age, email):        super().__init__(name, age)        self.email = email    def about(self):        return super().about()+f"\nMy email is: {self.email}"teacher = Teacher('sagor', 36, 'sagor@gmail.com')print(teacher.about())